import React from "react";
import Phrase from "../../Models/Phrase";
import DocumentUploader from "../DocumentUploader";

const TranslationMemory = () => {

  const [phrases, setPhrases] = React.useState<Phrase[]>();
  const [currentPhrase, setCurrentPhrase] = React.useState<Phrase>();

  const [reveal, setReveal] = React.useState<Boolean>(false);

  const handleReveal = (event: any) => {
    setReveal(!reveal);
  }

  const handleRestart = () => {
    console.log("Beginnning a new game");
  }

  const translationMemoryContext = React.createContext();

  return (
    <>
      <header>
        <h1>
          The Phrase Translation Memory Game
        </h1>
        <img src="../Cartoon.png" width="200"/>
      </header>

      <translationMemoryContext.Provider 
        value={{
          phrases, setPhrases, 
          currentPhrase, setCurrentPhrase,
          reveal, setReveal
        }}>

      <div id="new_game">
        <p>
          Upload a CSV with columns English Text, Translated Text, and Included 
        </p>
        <DocumentUploader />
      </div>

      <div id="in_game">
        <p>
          Write the translation with correct spelling:
        </p>
        <p>
          {currentPhrase?.english_text}
        </p>
        <button id="reveal" onClick={handleReveal}>
          Reveal Translation
        </button>
      </div>

      <div id="end_game">
        <p>
          You've gotten all translations correct!
        </p>
        <button id="restart" onClick={handleRestart}>
          Restart
        </button>
      </div>

      </translationMemoryContext.Provider>
    </>
  )
}

export default TranslationMemory;
