import React, { useContext, useEffect } from "react";
import Phrase from "../../../Models/Phrase";
import { translationMemoryContext } from "../TranslationMemory";

const InGame = () => {

  const translationMemoryStore = useContext(translationMemoryContext);
  const setCurrentPage = translationMemoryStore?.setCurrentPage;
  const phrases = translationMemoryStore?.phrases;

  const [reveal, setReveal] = React.useState<Boolean>(false);
  const [remainingPhrases, setRemainingPhrases] = React.useState<Phrase[]>(phrases as Phrase[]);
  const [currentPhrase, setCurrentPhrase] = react.useState<Phrase>({} as Phrase);

  const handleReveal = (event: any) => {
    console.log('Revealing translation');
    setReveal(!reveal);
  }

  const handleIncorrect = (event: any) => {
    console.log('Incorrect translation');
    // Load the next phrase
    nextPhrase();
  }

  const handleCorrect = (event: any) => {
    console.log('Correct translation');
    // If the user gets it correct, then add that phrase to CorrectPhrases

    // Load the next phrase
    nextPhrase();
  }

  const nextPhrase = () => {
    if(phrases?.length == 0){
      setCurrentPage? setCurrentPage('end_game'): console.error('There are no more phrases left');
    }
    setCurrentPhrase(phrases?.pop())
    setReveal(false);
  }

  useEffect(() => {
    nextPhrase();
  }, []);



  return(
    <>
        <div id="in_game">
        <p id="instructions">
          Write the translation with correct spelling:
        </p>
        <p id="english_text">
          {currentPhrase?.english_text}
        </p>
        <button id="reveal" onClick={handleReveal}>
          Reveal Translation
        </button>
        {reveal &&
          <div id="revealable_component">
          <p id="translated_text">
            {currentPhrase?.translation}
          </p>
          <button id="incorrect_button" onClick={handleIncorrect}>
            Incorrect
          </button>
          <button id="correct_button" onClick={handleCorrect}>
            Correct
          </button>
        </div>
        }
      </div>
    </>
  )
}

export default InGame
