import Papa from "papaparse";
import React, { useContext } from "react";
import Phrase from "../../../Models/Phrase";
import { translationMemoryContext } from "../TranslationMemory";


const DocumentUploader = () => {

  const translationMemoryStore = useContext(translationMemoryContext);
  const setPhrases = translationMemoryStore?.setPhrases;

  const changeHandler = (event: any) => {
    console.log(event.target.files[0]);
    parseCsv(event);
  };

  const parseCsv = (event: any) => {
    let phrases: Phrase[] = [];
    // Passing file data (event.target.files[0]) to parse using Papa.parse
    Papa.parse(event.target.files[0], {
      header: true,
      skipEmptyLines: true,
      complete: function (results: { data: any; }) {
        console.log(results.data);
        phrases = results.data;
        // console.log('raw data:\n'+ JSON.stringify(raw_data));
        setPhrases? setPhrases(phrases): console.log('Unable to initialize uploaded phrases');
      },
    });
  }


  return (
    <>
    <div>
    <input
          type="file"
          name="file"
          accept=".csv"
          onChange={changeHandler}
          style={{ display: "block", margin: "10px auto" }}
        />
    </div>
    </>
  )
}

export default DocumentUploader;
