import React, { ReactNode } from "react";
import { JsxElement } from "typescript";
import Phrase from "../../Models/Phrase";
import EndGame from "./EndGame/EndGame";
import InGame from "./InGame/InGame";
import DocumentUploader from "./NewGame/DocumentUploader";
import NewGame from "./NewGame/NewGame";

const TranslationMemory = () => {

  type GameState = {
    gameState: string,
    setGameState: Function,
    phrases: Phrase[],
    setPhrases: Function,
    currentPhrase: Phrase,
    setCurrentPhrase: Function,
  }

  const [gameState, setGameState] = React.useState<string>('in_game');
  const [phrases, setPhrases] = React.useState<Phrase[]>();
  const [currentPhrase, setCurrentPhrase] = React.useState<Phrase>();

  const [reveal, setReveal] = React.useState<Boolean>(false);

  const handleReveal = (event: any) => {
    setReveal(!reveal);
  }

  const handleRestart = () => {
    console.log("Beginnning a new game");
  }

  // const translationMemoryContext = React.createContext<GameState>(undefined);

  const gameStateMap = new Map<string, ReactNode>(); 
  gameStateMap.set('new_game', <NewGame/>);
  gameStateMap.set('in_game', <InGame/>);
  gameStateMap.set('end_game', <EndGame/>);

  return (
    <>
      <div id="game_state">
        {gameStateMap.get(gameState)}
      </div>
    </>



